const execBuffer = require('exec-buffer');
const jpegRecompress = require('jpeg-recompress-bin');

module.exports = buffer => execBuffer({
  input: buffer,
  bin: jpegRecompress,
  args: [
    '--strip',
    '--quality', 'medium',
    '--min', 40,
    '--max', 80,
    execBuffer.input,
    execBuffer.output
  ]
});
